// Loop over input items and add a new field called 'myNewField' to the JSON of each one
try {
  const items = $input.all();

  // Transformar la info para preparar texto para el embedding
  /**
   * Juego:nombre_juego. Género:genero_del_juego
   * CUIDADO AL QUITAR LOS ESPACIOS EN BLANCO, TABULADORES, ETC.. , DEL RESULTADO
   */

  const data = items.map(objeto => ({
    pageContent: `Juego: ${objeto.json.nombre}.
Género: ${objeto.json.genero}.
Descripción: ${objeto.json.descripcion}.
Temática: ${objeto.json.tematica}.`,
    ...objeto.json,
  }));

  return data;

} catch (error) {
  return [
    {
      json: {
        error: true,
        message: error.message,
        stack: error.stack,
      },
    },
  ];
}

